$(function() {
    var t = $(
        "iframe[src*='//player.vimeo.com'], iframe[src*='//www.youtube.com'], object, embed"
    );
    $("p");
    t.each(function() {
        $(this)
            .attr("data-aspectRatio", this.height / this.width)
            .removeAttr("height")
            .removeAttr("width");
    }),
        $(window)
            .resize(function() {
                t.each(function() {
                    var t = $(this),
                        e = t.parents("p").width();
                    t.width(e).height(e * t.attr("data-aspectRatio"));
                });
            })
            .resize();
});
